import React, {useRef} from 'react'
import {Link} from 'react-router-dom'
import '../styles/Main.css'
import { useDispatch } from 'react-redux'
import { setUserId } from '../redux/result_reducer'

export default function Main(){

    const inputRef = useRef(null)
    const dispatch = useDispatch()

    function startQuiz(){
        if(inputRef.current?.value){
             dispatch(setUserId(inputRef.current?.value))
        }
    }

    return (
        <div className='container'>
            <h1 className='title text-light'>Quiz Application</h1>
        

        <ol>
            <li> You will be asked a number of questions</li>
            <li> answer them before the timer expires</li>
            <li> Try to pass</li>
            <li> Your Life depends on it</li>
            <li> this is no joke</li>
        </ol>

        <form id='form'>
            <input ref={inputRef} type="text" className="userid" placeholder='Username*'></input>
        </form>

        <div>
            <Link className='btn' to={'quiz'} onClick={startQuiz}>Start Exam</Link>
        </div>
    </div>
    )
}
