import React, { useState, useEffect } from 'react';
import { getServerData, postServerData } from '../helper/helper';
import '../styles/ResultEdit.css'

export default function ResultEdit() {
    const [results, setResults] = useState([]);
    const [editedResults, setEditedResults] = useState([]);

    // Fetch existing results from the database
    useEffect(() => {
        const fetchData = async () => {
            try {
                const data = await getServerData('http://localhost:5000/api/result');
                setResults(data);
                setEditedResults(data); // Initialize editedResults with fetched data
            } catch (error) {
                console.error('Error fetching results:', error);
            }
        };
        fetchData();
    }, []);

    // Handle changes to edited results
    const handleEdit = (index, field, value) => {
        const updatedResults = [...editedResults];
        updatedResults[index][field] = value;
        setEditedResults(updatedResults);
    };

    // Save edited results to the database
    const saveEditedResults = async () => {
        try {
            await postServerData('http://localhost:5000/api/result', editedResults);
            // Optionally, update state or show a success message
        } catch (error) {
            console.error('Error saving edited results:', error);
        }
    };

    return (
        <div className="result-edit-container">
        {/* Display existing results and editing interface */}
        {results.map((result, index) => (
            <div className="result-item" key={index}>
                <input
                    type="text"
                    value={editedResults[index].username}
                    onChange={(e) => handleEdit(index, 'username', e.target.value)}
                />
                {/* Add more input fields for other result fields */}
            </div>
        ))}
        {/* Button to save edited results */}
        <button className="save-button" onClick={saveEditedResults}>Save Edited Results</button>
    </div>
    );
}
