import React, { useEffect } from 'react';
import { useFetchQuestion } from '../hooks/FetchQuestion';

const QuestionView = () => {
  const [getData, setGetData] = useFetchQuestion();

  useEffect(() => {
    // Trigger the fetch question function when the component mounts
    setGetData();
  }, [setGetData]);

  return (
    <div>
      {getData.isLoading ? (
        <div>Loading...</div>
      ) : getData.serverError ? (
        <div>Error: {getData.serverError.message}</div>
      ) : (
        <div>
          <h1>Questions</h1>
          <ul>
            {getData.apiData.questions.map((question, index) => (
              <li key={index}>
                <h3>Question {index + 1}</h3>
                <p>{question}</p>
                <h4>Answers:</h4>
                <ul>
                  {getData.apiData.answers[index].map((answer, answerIndex) => (
                    <li key={answerIndex}>{answer}</li>
                  ))}
                </ul>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default QuestionView;
