import React, { useState, useEffect } from 'react';
import { getServerData, putServerData } from '../helper/helper';

export default function EditableResultView() {
    const [data, setData] = useState([]);

    useEffect(() => {
        getServerData('http://localhost:5000/api/result', (res) => {
            setData(res);
        });
    }, []);

    const handleInputChange = (index, field, value) => {
        // Update the data state with the modified value
        const updatedData = [...data];
        updatedData[index][field] = value;
        setData(updatedData);
    };

    const handleSaveResult = async (index) => {
        try {
            const editedResult = data[index];
            await putServerData('http://localhost:5000/api/result', editedResult);
            console.log('Result updated successfully');
        } catch (error) {
            console.error('Error updating result:', error);
        }
    };

    return (
        <div className="editable-result-view">
            <h2>Editable Results</h2>
            {data.length === 0 ? (
                <p>No results found.</p>
            ) : (
                <ul>
                    {data.map((result, index) => (
                        <li key={result._id}>
                            <strong>ID:</strong> {result._id}
                            <br />
                            <strong>Name:</strong>
                            <input
                                type="text"
                                value={result.username}
                                onChange={(e) =>
                                    handleInputChange(index, 'username', e.target.value)
                                }
                            />
                            <strong>Attempts:</strong>
                            <input
                                type="number"
                                value={result.attempts}
                                onChange={(e) =>
                                    handleInputChange(index, 'attempts', e.target.value)
                                }
                            />
                            <strong>Marks:</strong>
                            <input
                                type="number"
                                value={result.points}
                                onChange={(e) =>
                                    handleInputChange(index, 'points', e.target.value)
                                }
                            />
                            <button onClick={() => handleSaveResult(index)}>Save</button>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}
