import { useState, useEffect } from "react";
import { useEditQuestion } from "../hooks/EditQuestion";

function ExamComponent() {
    const [getData, setGetData] = useEditQuestion(); // Fetch questions using the custom hook
    const [questionsAndAnswers, setQuestionsAndAnswers] = useState([]); // State to hold questions and answers

    // Update questions and answers state when data is fetched
    useEffect(() => {
        if (!getData.isLoading && !getData.serverError) {
            setQuestionsAndAnswers(getData.apiData);
        }
    }, [getData]);

    const handleInputChange = (index, field, value) => {
        const updatedQuestionsAndAnswers = [...questionsAndAnswers];
        updatedQuestionsAndAnswers[index][field] = value;
        setQuestionsAndAnswers(updatedQuestionsAndAnswers);
    };

    const handleSubmit = () => {
        // Handle submission logic here, such as sending edited data to the server
        console.log('Edited questions and answers:', questionsAndAnswers);
    };

    return (
        <div>
            {getData.isLoading ? (
                <p>Loading...</p>
            ) : getData.serverError ? (
                <p>Error: {getData.serverError.message}</p>
            ) : (
                <div>
                    {questionsAndAnswers.map((item, index) => (
                        <div key={index}>
                            <p>Question {index + 1}:</p>
                            <input
                                type="text"
                                value={item.question}
                                onChange={(e) => handleInputChange(index, 'question', e.target.value)}
                            />
                            <p>Answer:</p>
                            <input
                                type="text"
                                value={item.answer}
                                onChange={(e) => handleInputChange(index, 'answer', e.target.value)}
                            />
                        </div>
                    ))}
                    <button onClick={handleSubmit}>Submit</button>
                </div>
            )}
        </div>
    );
}

export default ExamComponent;
